import DefaultData from "./DefaultData";
import WeatherApiInfo from "./WeatherApiInfo";

const WeatherData = () => {
    const currentLocationData = DefaultData;
    const currentLocation = "London";

    this.getDataForLocation = (loc) => {
        if (loc !== currentLocationData) {
            currentLocation = loc;
            currentLocationData = DefaultData;
        }
    };

    const getCurrentConditions = () => {
        const { current } = currentLocationData;
        const dayConditions = currentLocationData.forecast.forecastday[0].day;
        const { mintemp_f, maxtemp_f, mintemp_c, maxtemp_c } = dayConditions;
        [
            current.mintemp_f,
            current.maxtemp_f,
            current.mintemp_c,
            current.maxtemp_c,
        ] = [mintemp_f, maxtemp_f, mintemp_c, maxtemp_c];

        return current;
    };

    const getForecast = () => {
        return currentLocationData.forecast;
    };

    return DefaultData;
};

export default WeatherData;
