import React, { useEffect, useState } from "react";
import "../css/Menu.css";

const MenuSearchBar = ({ setLocation }) => {
    const [newLocation, setNewLocation] = useState("");

    useEffect(() => {
        const searchForm = document.getElementById("menu__search__form");
        searchForm.addEventListener("submit", setNewLocation(searchForm.value));
        console.log(searchForm.value);
        return () => {
            searchForm.removeEventListener(
                "submit",
                setNewLocation(searchForm.value)
            );
        };
    }, []);

    useEffect(() => {
        setLocation(newLocation);
        console.log(newLocation);
    }, [newLocation]);

    return (
        <div className="menu__search__bar" id="menu__search__form">
            <form id="menu__search__form">
                <div className="menu__search__bar--container">
                    <input
                        type="text"
                        name=""
                        id="menu__search__box"
                        placeholder="Search for a new location..."
                        autoComplete="off"
                    />
                </div>
            </form>
        </div>
    );
};

export default MenuSearchBar;
