import React, { useEffect, useState } from "react";
import "../css/Menu.css";

const MenuSearchBar = ({ setLocation }) => {
    const [newLocation, setNewLocation] = useState("");

    const handleSubmit = () => {
        const searchForm = document.getElementById("menu__search__form");
        console.log(searchForm.elements["search__input"].value);
    };

    useEffect(() => {
        setLocation(newLocation);
        console.log(newLocation);
    }, [newLocation]);

    return (
        <div className="menu__search__bar">
            <form id="menu__search__form" onSubmit={() => handleSubmit}>
                <div className="menu__search__bar--container">
                    <input
                        type="text"
                        name="search__input"
                        id="menu__search__box"
                        placeholder="Search for a new location..."
                        autoComplete="off"
                    />
                </div>
            </form>
        </div>
    );
};

export default MenuSearchBar;
