import React, { useEffect, useState } from "react";
import "../css/Menu.css";

const MenuSearchBar = ({ setLocation }) => {
    const [newLocation, setNewLocation] = useState("");

    const handleSubmit = () => {
        const searchBox = document.getElementById("menu__search__box");
        console.log(searchBox.value);
        searchBox.value = "";

        return false;
    };

    useEffect(() => {
        setLocation(newLocation);
        console.log("in use effect", newLocation);
    }, [newLocation]);

    return (
        <div className="menu__search__bar">
            <div className="menu__search__bar--container">
                <input
                    type="text"
                    name="search__input"
                    id="menu__search__box"
                    placeholder="Search for a new location..."
                    autoComplete="off"
                    onKeyPress={(e) =>
                        e.key == "Enter" ? handleSubmit() : null
                    }
                />
            </div>
        </div>
    );
};

export default MenuSearchBar;
