import DefaultData from "./DefaultData";
import WeatherApiInfo from "./WeatherApiInfo";

const WeatherData = () => {
    this.currentLocationData = DefaultData;
    this.currentLocation = "London";

    this.getDataForLocation = (loc) => {
        if (loc !== this.currentLocationData) {
            this.currentLocation = loc;
            this.currentLocationData = DefaultData;
        }
    };

    this.getCurrentConditions = () => {
        const { current } = this.currentLocationData;
        const dayConditions = this.currentLocationData.forecast.forecastday[0]
            .day;
        const { mintemp_f, maxtemp_f, mintemp_c, maxtemp_c } = dayConditions;
        [
            current.mintemp_f,
            current.maxtemp_f,
            current.mintemp_c,
            current.maxtemp_c,
        ] = [mintemp_f, maxtemp_f, mintemp_c, maxtemp_c];

        return current;
    };

    this.getForecast = () => {
        return this.currentLocationData.forecast;
    };
};

export default WeatherData;
