import React, { useState } from "react";
import "../css/App.css";
import MainTile from "./MainTile";
import WeatherData from "../WeatherData";
import DefaultData from "./DefaultData";

const App = () => {
    const [currentLocation, setLocation] = useState("London");
    const [currentLocationData, setLocationData] = useState(DefaultData);
    const [currentConditions, setCurrentConditions] = useState(
        () => getCurrentConditions
    );
    const [forecast, setForecast] = useState(() => getForecast);

    const getCurrentConditions = () => {
        const { current } = currentLocationData;
        const dayConditions = currentLocationData.forecast.forecastday[0].day;
        const { mintemp_f, maxtemp_f, mintemp_c, maxtemp_c } = dayConditions;
        [
            current.mintemp_f,
            current.maxtemp_f,
            current.mintemp_c,
            current.maxtemp_c,
        ] = [mintemp_f, maxtemp_f, mintemp_c, maxtemp_c];

        return current;
    };

    this.getForecast = () => {
        return currentLocationData.forecast;
    };

    return (
        <div className="app">
            <MainTile weatherData={WeatherData} />
        </div>
    );
};

export default App;
